import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import axios from '../services/api';
import { Link } from 'react-router-dom';

const AddMaster = () => {
    const { isAuthenticated } = useContext(AuthContext);
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [specialization, setSpecialization] = useState('');
    const [photo, setPhoto] = useState('');
    const [city, setCity] = useState('');
    const [service, setService] = useState('');
    const [services, setServices] = useState([]);

    useEffect(() => {
        const fetchServices = async () => {
            const res = await axios.get('/api/services');
            setServices(res.data);
        };
        fetchServices();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token'); // Get token from localStorage
            const res = await axios.post('/api/masters', { firstName, lastName, specialization, photo, city, service }, {
                headers: {
                    'x-auth-token': token
                }
            });
            console.log(res.data);
        } catch (err) {
            console.error(err);
        }
    };

    if (!isAuthenticated) {
        return (
            <div className="login-page">
                <h2>Please log in to add a master</h2>
                <Link to="/login">Login</Link>
                <p>If you are not registered, please <Link to="/register">register here</Link>.</p>
            </div>
        );
    }

    return (
        <div className="add-master-page">
            <h2>Add Master</h2>
            <form onSubmit={handleSubmit}>
                <input 
                    type="text" 
                    value={firstName} 
                    onChange={(e) => setFirstName(e.target.value)} 
                    placeholder="First Name" 
                />
                <input 
                    type="text" 
                    value={lastName} 
                    onChange={(e) => setLastName(e.target.value)} 
                    placeholder="Last Name" 
                />
                <input 
                    type="text" 
                    value={specialization} 
                    onChange={(e) => setSpecialization(e.target.value)} 
                    placeholder="Specialization" 
                />
                <input 
                    type="text" 
                    value={photo} 
                    onChange={(e) => setPhoto(e.target.value)} 
                    placeholder="Photo URL" 
                />
                <input 
                    type="text" 
                    value={city} 
                    onChange={(e) => setCity(e.target.value)} 
                    placeholder="City" 
                />
                <select value={service} onChange={(e) => setService(e.target.value)}>
                    <option value="">Select Service</option>
                    {services.map(s => (
                        <option key={s._id} value={s._id}>{s.name}</option>
                    ))}
                </select>
                <button type="submit">Add Master</button>
            </form>
        </div>
    );
};

export default AddMaster;
